import React from "react"
import { makeStyles } from '@material-ui/core/styles';
import imageApp from '../../assets/projects/iphone2.png'


const ColoredLine = ({ color }) => (
  <hr
    style={{
      color: color,
      backgroundColor: color,
      height: 3,
      width: '80%'
    }}
  />
);

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  Page: {
    width: '80%',
    margin: 'auto',
    paddingBottom: '3em',
    height: '100%',
    [theme.breakpoints.down('xs')]: {
      width: '95%',
    },
  },
  TextTitle: {
    fontFamily: 'Raleway, sans-serif',
    fontSize: '2em',
    textAlign: 'center',
    color: 'black',
    margin: 'auto',
    marginBottom: '30px',
    // paddingBottom: '10px',
    paddingTop: '1em'
  },
  Text: {
    fontFamily: 'Lato, sans-serif',
    fontSize: '1em',
    textAlign: 'center',
    color: 'black',
    marginTop: '-10px',
    [theme.breakpoints.down('xs')]: {
      textAlign: 'left',

    },
  },
  BackgroundHighlight1: {
    backgroundColor: 'yellow',
  },
  ImageLogo: {
    width: '50%',
    marginBottom: '3em',
    [theme.breakpoints.down('xs')]: {
      width: '80%',

    },
  }


}));
function Calculator(props) {
  const classes = useStyles();
;

  return (
    <div className={classes.Page}>
      <h1 className={classes.TextTitle}>Unit Converter</h1>
      <p className={classes.Text}>	The app allows you to convert weights, temperature, volumes, liquid volumes, distance and speed.  <br />
        <br /><br />
      </p>
      <div >
        <img className={classes.ImageLogo} src={imageApp} alt="screenshots"/>
      </div>
      <ColoredLine color='black' />
      <div>...</div>
      <p className={classes.Text}>
        <b className={classes.BackgroundHighlight1}>Main Functionalities</b><br /><br/>
        - Custom Keyboard creadted from scratch<br />
        - History functionality to review the last 5 conversion maded<br />
        - Most popular units covered<br />
        <br /><br />
        <b className={classes.BackgroundHighlight1}>Key Components used</b><br/><br/>
        - Scroll Views<br />
        - Core Graphics <br />
        - User Defaults<br />
        - Custom View<br />
        - IBDesignable, IBInspectable<br />
        - Delegate<br />
        - MVC<br />
      </p>
    </div>
  )
}

export default Calculator;
