import React from "react"
import AwesomeSlider from 'react-awesome-slider';
import 'react-awesome-slider/dist/styles.css';
import pic1 from '../../assets/img1.jpg'
import pic2 from '../../assets/img2.jpg'
import pic3 from '../../assets/img3.jpg'
import pic4 from '../../assets/img4.jpg'
import pic5 from '../../assets/img5.jpg'
import pic6 from '../../assets/img6.jpg'
import pic7 from '../../assets/img7.jpg'
import pic8 from '../../assets/img8.jpg'

import { makeStyles } from '@material-ui/core/styles';
const useStyles = makeStyles(theme => ({

    Page: {
        width: '80%',
        margin: 'auto',
        paddingBottom: '3em',
        height: '100%',
        [theme.breakpoints.down('xs')]: {
            width: '95%',
        },
    },
    Text: {
        fontFamily: 'Raleway, sans-serif',
                fontSize: '2em',
        textAlign: 'left',
        color: 'black',
        margin: 'auto',
        marginBottom:'30px',
        // paddingBottom: '10px',
        // paddingTop: '10px'
    },
    TextTitle2: {
        fontFamily: 'Lato, sans-serif',
        fontSize: '1em',
        textAlign: 'center',
        color: 'black',
        marginTop: '0px',
        [theme.breakpoints.down('xs')]: {
            textAlign: 'left',

        },
        // paddingTop: '5px',
        // paddingBottom: '5px',
    },
    BackgroundHighlight1:{
        backgroundColor: 'yellow',
    },
    BackgroundHighlight2:{
        backgroundColor: '#F8E59A',
    },

}));




function Photography(props) {
    const classes = useStyles();
    return (
        <div className={classes.Page}>
            <h1 className={classes.Text}>Hobbies</h1>
            <p className={classes.TextTitle2}><b className={classes.BackgroundHighlight1}>Piano / Guitar </b>- I started to learn music at 7 yo and I've never stopped!</p>
            <p className={classes.TextTitle2}><b className={classes.BackgroundHighlight1}>Photography</b> - I love shooting with prime lens, <b className={classes.BackgroundHighlight2}>35 and 75mm</b> to capture life. </p>
            <p className={classes.TextTitle2}><i>“The best thing about a picture is that it never changes, even when the people in it do.”</i> — Andy Warhol</p>
            <AwesomeSlider className={classes.Photos}>
                <div data-src={pic1} />
                <div data-src={pic2} />
                <div data-src={pic3} />
                <div data-src={pic4} />
                <div data-src={pic5} />
                <div data-src={pic6} />
                <div data-src={pic7} />
                <div data-src={pic8} />
            </AwesomeSlider>
        </div>
    )
}

export default Photography;
