import { makeStyles } from '@material-ui/core/styles';
import Colours from '../../components/style/Colours.js'

const DefinedColours = new Colours();

const useStylesGlobal = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    WelcomeTitle: {
        fontFamily: 'Poppins, sans-serif',
        fontWeight: '600',
        fontSize: '8em',
        textAlign: 'center',
        color: DefinedColours.colorBlack,
        margin: 'auto',
        wordSpacing: '9px',
        marginBottom: '30px',
        paddingTop: '30px',
        [theme.breakpoints.down('sm')]: {
            fontSize: '7em',

        },
        [theme.breakpoints.down('xs')]: {
            fontSize: '5em',
            lineHeight: '120px',

        },
    },
    WelcomeText: {
        fontFamily: 'Poppins, sans-serif',
        fontWeight: '400',
        textAlign: 'center',
        wordSpacing: '9px',
        color: DefinedColours.colorBlack,
    },
    TextTitle: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '2em',
        textAlign: 'center',
        color: DefinedColours.colorBlack,
        margin: 'auto',
        marginBottom: '30px',
        // paddingBottom: '10px',
        paddingTop: '1em'
    },
    // SUBTITLE OF PROJECTS
    TextSubTitle: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '1em',
        color: DefinedColours.color4,
        fontWeight: '600',
        margin: 'auto',
        marginBottom: '30px',
        // paddingBottom: '10px',
        paddingTop: '1em',
        backgroundColor: DefinedColours.color5,
    },
    Text: {
        fontFamily: 'Lato, sans-serif',
        fontSize: '1em',
        textAlign: 'left',
        color: DefinedColours.colorBlack,
        marginTop: '-10px',
        lineHeight: '2',
        paddingBottom: '17px',
        [theme.breakpoints.down('xs')]: {
            textAlign: 'left',

        },
    },

    TextSkills: {
        fontFamily: 'Lato, sans-serif',
        fontSize: '1em',
        textAlign: 'center',
        color: DefinedColours.colorBlack,
        marginTop: '-2px',
        marginBottom: '-0.5px',

        [theme.breakpoints.down('xs')]: {
            textAlign: 'left',

        },
    },
    BackgroundHighlight1: {
        backgroundColor: DefinedColours.color4,
    },
    BackgroundHighlight2:{
        backgroundColor: DefinedColours.color3,
    },
    ImageLogo: {
        width: '50%',
        marginBottom: '3em',
        [theme.breakpoints.down('xs')]: {
            width: '80%',

        },
    },
    ImageMyFace:{
        width: '80%',
        textAlign: 'center',
        paddingTop: '2em',
        // marginBottom: '3em',
        [theme.breakpoints.down('xs')]: {
            width: '60%',

        },
    },
    textLinks: {

        color: 'black',
        fontWeight: '800',
        textDecoration: 'none',
        borderBottom: '3px solid',
        borderColor: DefinedColours.color2,
        paddingBottom: '3px',
        '&:hover': {
          backgroundImage: 'linear-gradient(to right, '+ DefinedColours.color5+', '+ DefinedColours.color4+')',
          color: '#03001e',
          borderBottom: '3px solid black',
          paddingBottom: '5px',
          paddingTop:'2px',
          transition: '0.5s'
       },
      },


}));

export default useStylesGlobal;
