import { makeStyles } from '@material-ui/core/styles';
import Colours from '../../components/style/Colours.js'

const DD = Colours();

const useStylesGlobal = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    WelcomeTitle: {
        fontFamily: 'Poppins, sans-serif',
        fontWeight: '600',
        fontSize: '8em',
        textAlign: 'center',
        color: 'black',
        color: DD.color1,
        margin: 'auto',
        wordSpacing: '9px',
        marginBottom: '30px',
        paddingTop: '30px',
        [theme.breakpoints.down('sm')]: {
            fontSize: '7em',

        },
        [theme.breakpoints.down('xs')]: {
            fontSize: '5em',
            lineHeight: '120px',

        },
    },
    WelcomeText: {
        fontFamily: 'Poppins, sans-serif',
        fontWeight: '400',
        textAlign: 'center',
        wordSpacing: '9px',
        color: 'black',
    },
    TextTitle: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '2em',
        textAlign: 'center',
        color: 'black',
        margin: 'auto',
        marginBottom: '30px',
        // paddingBottom: '10px',
        paddingTop: '1em'
    },
    TextSubTitle: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '1em',
        // textAlign: 'center',
        color: 'black',
        margin: 'auto',
        marginBottom: '30px',
        // paddingBottom: '10px',
        paddingTop: '1em',
        backgroundColor: '#F8E59A',
    },
    Text: {
        fontFamily: 'Lato, sans-serif',
        fontSize: '1em',
        textAlign: 'left',
        color: 'black',
        marginTop: '-10px',
        lineHeight: '2',
        paddingBottom: '17px',
        [theme.breakpoints.down('xs')]: {
            textAlign: 'left',

        },
    },

    TextSkills: {
        fontFamily: 'Lato, sans-serif',
        fontSize: '1em',
        textAlign: 'center',
        color: 'black',
        marginTop: '-2px',
        marginBottom: '-0.5px',

        [theme.breakpoints.down('xs')]: {
            textAlign: 'left',

        },
    },
    BackgroundHighlight1: {
        backgroundColor: 'yellow',
    },
    BackgroundHighlight2:{
        backgroundColor: '#F8E59A',
    },
    ImageLogo: {
        width: '50%',
        marginBottom: '3em',
        [theme.breakpoints.down('xs')]: {
            width: '80%',

        },
    },
    ImageMyFace:{
        width: '80%',
        textAlign: 'center',
        paddingTop: '2em',
        // marginBottom: '3em',
        [theme.breakpoints.down('xs')]: {
            width: '60%',

        },
    },
    textLinks: {
        color: 'black',
        fontWeight: '800',
        textDecoration: 'none',
        borderBottom: '3px solid #74ebd5',
        paddingBottom: '3px',
        '&:hover': {
          backgroundImage: 'linear-gradient(to right, #74ebd5, #ACB6E5)',
          color: '#03001e',
          borderBottom: '3px solid black',
          paddingBottom: '5px',
          paddingTop:'2px',
          transition: '0.5s'
       },
      },


}));

export default useStylesGlobal;
