import React from "react"
import { makeStyles } from '@material-ui/core/styles';
import imageApp from '../../assets/projects/forecasting_button.png'

const ColoredLine = ({ color }) => (
  <hr
    style={{
      color: color,
      backgroundColor: color,
      height: 3,
      width: '80%'
    }}
  />
);

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  Page: {
    width: '80%',
    margin: 'auto',
    paddingBottom: '3em',
    height: '100%',
    [theme.breakpoints.down('xs')]: {
      width: '95%',
    },
  },
  TextTitle: {
    fontFamily: 'Raleway, sans-serif',
    fontSize: '2em',
    textAlign: 'center',
    color: 'black',
    margin: 'auto',
    marginBottom: '30px',
    // paddingBottom: '10px',
    paddingTop: '1em'
  },
  Text: {
    fontFamily: 'Lato, sans-serif',
    fontSize: '1em',
    textAlign: 'center',
    color: 'black',
    marginTop: '-10px',
    [theme.breakpoints.down('xs')]: {
      textAlign: 'left',

    },
  },
  BackgroundHighlight1: {
    backgroundColor: 'yellow',
  },
  ImageLogo: {
    width: '50%',
    marginBottom: '3em',
    [theme.breakpoints.down('xs')]: {
      width: '80%',

    },
  }


}));
function StockForecasting(props) {
  const classes = useStyles();
;

  return (
    <div className={classes.Page}>
      <h1 className={classes.TextTitle}>Stock Price trend forecasting</h1>
      <p className={classes.Text}>	Forecasts based on historical prices are studied applying Linear Regression, Polynomial Regression, and Neural Network Backpropagation aimed to identify Elliott wave and Fibonacci ratios. <br/>
      People sentiment, as a factor of stock price fluctuation is studied analysing messages published on Twitter using a Naïve Bayes approach.
        <br /><br />
      </p>
      <div >
        <img className={classes.ImageLogo} src={imageApp} alt="screenshots"/>
      </div>
      <ColoredLine color='black' />
      <div>...</div>
      <p className={classes.Text}>
        <b className={classes.BackgroundHighlight1}>Main Functionalities</b><br /><br/>
        - daily automatic update of the data<br/>
			  - daily and intraday stock price data covered<br/>
			  - 6 forecasting algorithms implemented<br/>
			  - controller algorithm to identify the best algorithm to use for each stock<br/>
			  - detailed accuracy information of the forecasting algorithm executed<br/>
        <br /><br />
        <b className={classes.BackgroundHighlight1}>Key Components used</b><br/><br/>
			  - Flask microframework<br/>
        - Python backend<br/>
			  - HTML/JS/CSS interface<br/>
        - MySQL database<br/>
			  - ALL forecasting algorithms inplemented from scratch<br/>
			  - Docker solution<br/>
      </p>
    </div>
  )
}

export default StockForecasting;
