import React from "react"
import { makeStyles } from '@material-ui/core/styles';
import imageApp from '../../assets/projects/earth_button3.png'

const ColoredLine = ({ color }) => (
  <hr
    style={{
      color: color,
      backgroundColor: color,
      height: 3,
      width: '80%'
    }}
  />
);

const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },

  Page: {
    width: '80%',
    margin: 'auto',
    paddingBottom: '3em',
    height: '100%',
    [theme.breakpoints.down('xs')]: {
      width: '95%',
    },
  },
  TextTitle: {
    fontFamily: 'Raleway, sans-serif',
    fontSize: '2em',
    textAlign: 'center',
    color: 'black',
    margin: 'auto',
    marginBottom: '30px',
    // paddingBottom: '10px',
    paddingTop: '1em'
  },
  Text: {
    fontFamily: 'Lato, sans-serif',
    fontSize: '1em',
    textAlign: 'center',
    color: 'black',
    marginTop: '-10px',
    [theme.breakpoints.down('xs')]: {
      textAlign: 'left',

    },
  },
  BackgroundHighlight1: {
    backgroundColor: 'yellow',
  },
  ImageLogo: {
    width: '50%',
    marginBottom: '3em',
    [theme.breakpoints.down('xs')]: {
      width: '80%',

    },
  }


}));

function Earthquakes(props) {
  const classes = useStyles();
;

  return (
    <div className={classes.Page}>
      <h1 className={classes.TextTitle}>Earthquakes tracker</h1>
      <p className={classes.Text}>	The application is used to store and visualize earthquakes' information. <br/>
      It allows the final user to view all the specific details of the earthquake, the detail of the closest city affected by the earthquake, the image of the wave recorded by the near seismometer and if exist, news article related to the specific earthquake. <br/>
      In addition, it extracts and shows from the identified articles the sentences related to death or injuries.
        <br /><br />
      </p>
      <div >
        <img className={classes.ImageLogo} src={imageApp} alt="screenshots"/>
      </div>
      <ColoredLine color='black' />
      <div>...</div>
      <p className={classes.Text}>
        <b className={classes.BackgroundHighlight1}>Main Functionalities</b><br /><br/>
        - custom visualization of the earthquakes on the map based on the magnitude and affected population<br/>
			  - interactive map<br/>
			  - 40+ years covered, from 1980 - today<br/>
			  - earthquake data refreshed daily<br/>
			  - dynamic one-page design for the information display<br/>
        <br /><br />
        <b className={classes.BackgroundHighlight1}>Key Components used</b><br/><br/>
			  - Bash script to query and download periodically earthquake info using the API provided by the USGS<br/>
			  - HDFS | Fault Tolerance, Reliability, Scalability<br/>
			  - Hive | Java UDF<br/>
			  - MRJob | Python Map Reduce solutions<br/>
			  - Tableau<br/>
			  - Machine Learning Naive Bayes classifier<br/>
			  - 6 different datasource with semi-structured / unstructured data fromat<br/>
      </p>
    </div>
  )
}

export default Earthquakes;
