import React from 'react';
import '../../App.css';

import { Route, Link } from 'react-router-dom'; 
import { makeStyles } from '@material-ui/core/styles';


import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Projects from '../../pages/projects/Projects.js'; 
import Aboutme from '../../pages/Aboutme/Aboutme.js'; 
import ActivityTracker from '../../pages/projects/ActivityTracker.js'; 
import Calculator from '../../pages/projects/Calculator.js'; 
import StockForecasting from '../../pages/projects/StockForecasting.js'; 
import Earthquake from '../../pages/projects/Earthquake.js'; 
import MailOutlineIcon from '@material-ui/icons/MailOutline';

const useStyles = makeStyles(theme => ({
  menuButton: {
    width: '100px',
    backgroundColor: "#FFF",
    "&:hover": {
        //you want this to be the same as the backgroundColor above
        backgroundColor: "#FFF",
    },
    [theme.breakpoints.down('sm')]: {
      height: 30,
      fontSize: 10,

    },
    marginTop: 'auto',
    marginBottom: 'auto',
  },
  title: {
    width: '100px',
  },
  title_left: {
    marginLeft: 'auto',
    marginRight: '5px',
    width: '100px',
  },
  title_right: {
    marginRight: 'auto',
    width: '100px',
    color: 'black',
  },
  menuButtonLeft: {
    marginLeft: 'auto',
    marginRight: '5px',
    width: '100px',
    backgroundColor: "#FFF",
    "&:hover": {
        //you want this to be the same as the backgroundColor above
        backgroundColor: "#FFF",
    },
    [theme.breakpoints.down('sm')]: {
      height: 30,
      fontSize: 10,

    },
    marginTop: 'auto',
    marginBottom: 'auto',
  },
  menuButtonRight: {
    marginRight: 'auto',
    width: '100px',
    backgroundColor: "#FFF",
    "&:hover": {
        //you want this to be the same as the backgroundColor above
        backgroundColor: "#FFF",
    },
    [theme.breakpoints.down('sm')]: {
      height: 30,
      fontSize: 10,
    },
  },
  appbarLinks: {
    // color: theme_mine.palette.secondary.contrastText,
    textDecoration: 'none',
    color: '#00B176',
  },
  appbar: {
    backgroundColor: 'transparent',
    boxShadow: 'none',
    [theme.breakpoints.down('xs')]: {
      backgroundColor: 'white',
      height: 50,
      boxShadow: "1px 3px 1px #9E9E9E",
    },
    [theme.breakpoints.up('md')]: {
      backgroundColor: 'transparent',
      boxShadow: 'none',
    },
    [theme.breakpoints.up('lg')]: {
      backgroundColor: 'transparent',
      boxShadow: 'none',
    },
  },
}));


  function Topbar (props){
      const classes = useStyles();

    return(
        <div className="navigation">
          <AppBar position="sticky" className={classes.appbar}>
              <Toolbar>
                {/* <Typography variant="h6" className={classes.title_right}> <Link to="/" className={classes.appbarLinks}>Magic</Link> </Typography> */}
                <a href="mailto:demo@demo.com" color={'black'}><MailOutlineIcon className={classes.title_right}></MailOutlineIcon> Contact Me </a>
                <Typography variant="h6" className={classes.title_left}> <Link to="/" className={classes.appbarLinks}>About Me</Link> </Typography>
                <Typography variant="h6" className={classes.title}> <Link to="/Projects" className={classes.appbarLinks}>Projects</Link> </Typography>
              </Toolbar>
            </AppBar>
            {/* routing --> */}
            <Route path="/Projects" component={Projects} /> 
            <Route exact path="/" component={Aboutme}/>
            <Route path="/ActivityTracker" component={ActivityTracker} /> 
            <Route path="/Earthquake" component={Earthquake} /> 
            <Route path="/Calculator" component={Calculator} /> 
            <Route path="/StockForecasting" component={StockForecasting} /> 

          </div>
    )
  }


  export default Topbar;
