import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Colours from '../components/style/Colours.js'

const DefinedColours = new Colours();


const useStyles = makeStyles(theme => ({
    Text: {
        fontFamily: 'Lato, sans-serif',
        fontSize: '1em',
        textAlign: 'center',
        color: 'black',
        margin: 'auto',
        paddingBottom: '10px',
        paddingTop: '10px'
    },

    Footer: {
        height: '40px',
        background: 'linear-gradient(45deg, '+ DefinedColours.color4+' 30%, '+ DefinedColours.color2+' 90%)',
        [theme.breakpoints.down('sm')]: {
          height: '60px',
        },
    }
  }))

  function Footer(props) {
    const classes = useStyles();

    return (
      <div className={classes.Footer}>
          <h1 className={classes.Text}>© Tiziano Bissola 2019 ∞ Made with 100% recycled pixels. (ツ)</h1>
    </div>
    )
  }

  export default Footer;
